import time
import functools

def timeit(func):
    @functools.wraps(func)
    def wrapper(*args, **kwargs):
        start  = time.perf_counter()
        result = func(*args, **kwargs)
        end    = time.perf_counter()
        print(f'{func.__name__!r} took {end - start:.4f}s')
        return result
    return wrapper

@timeit
def slow_function():
    time.sleep(1)

slow_function()  # 'slow_function' took 1.0002s
